import React, { useState } from "react";
import "./Body.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faMagnifyingGlass,
  faMicrophone,
  faCamera,
} from "@fortawesome/free-solid-svg-icons";

function Body({ query, setQuery, handleFormSubmit }) {
  const [inputValue, setInputValue] = useState('');

  const handleChange = (e) => {
    setInputValue(e.target.value); // Update input value state as the user types
    setQuery(e.target.value); // Update query state
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter") {
      e.preventDefault(); // Prevent default form submission
      handleFormSubmit(); // Submit form programmatically
    }
  };

  const containerStyle = {
    backgroundColor: inputValue ? "#3498db" : "transparent", // Set background color based on input value
  };

  return (
    <div className="container" style={containerStyle}>
      <div>
        <h1 className="google-text">
          <span className="g">G</span>
          <span className="o1">o</span>
          <span className="o2">o</span>
          <span className="g2">g</span>
          <span className="l">l</span>
          <span className="e">e</span>
        </h1>
      </div>
      <div id="inputWrapper">
        <div className="search-bar">
          <div className="search-icon">
            <FontAwesomeIcon icon={faMagnifyingGlass} />
          </div>
          <form onSubmit={handleFormSubmit}>
            <input
              id="input"
              type="text"
              value={query}
              onChange={handleChange} // Handle input change
              onKeyDown={handleKeyDown} // Handle Enter key press
              placeholder="Search Google or type a URL"
            />
            <button type="submit" onClick={(e) => e.preventDefault()}>
              Search
            </button>
          </form>
          <div className="icons">
            <FontAwesomeIcon
              icon={faMicrophone}
              style={{ color: "#34a853" }}
            />
            <FontAwesomeIcon icon={faCamera} style={{ color: "#4285f4" }} />
          </div>
        </div>
      </div>
    </div>
  );
}

export default Body;
