// App.js
import React, { useState } from 'react';
import Header from './components/Header';
import SearchResult from './components/SearchResult';
import Footer from './components/Footer';
import Body from './components/Body';
// import { getSearchResults } from './api';

function App() {
  const [query, setQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  const handleFormSubmit = async (event) => {
    event.preventDefault(); // Prevent default form submission behavior
    if (query.trim() !== '') {
      try {
        const results = await fetchSearchResults(query); // Fetch search results from API
        setSearchResults(results); // Set search results in state
      } catch (error) {
        console.error('Error fetching search results:', error);
      }
    }
  };

  const fetchSearchResults = async (query) => {
    try {
      const response = await fetch(`https://api.example.com/search?q=${encodeURIComponent(query)}`);
      if (!response.ok) {
        throw new Error('Failed to fetch search results');
      }
      const data = await response.json();
      return data.results; // Assuming the API response contains a results array
    } catch (error) {
      throw new Error('Error fetching search results:', error);
    }
  };


  return (
    <div className="App">
      <Body query={query} setQuery={setQuery} handleSearch={handleFormSubmit} />

      <SearchResult  />
      {/* <Footer /> */}
    </div>
  );
}

export default App;


